#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long count[100001] = {0};
    for (int i = 0; i < n; i++) {
        if (a[i] <= 100000)
            count[a[i]]++;
    }
    long long max_area = 0;
    for (long long i = 1; i <= 100000; i++) {
        if (count[i] >= 2) {
            for (long long j = i + 1; j <= 100000; j++) {
                if (count[j] >= 2) {
                    max_area = (max_area > i * j) ? max_area : i * j;
                }
            }
        }
    }
    printf("%lld\n", max_area);
    return 0;
}

