#include <iostream>
#include <vector>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;

  map<long long, int> counts;
  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    counts[a[i]]++;
  }

  long long max_area = 0;
  vector<long long> sides;
  for (auto const& [key, val] : counts) {
    if (val >= 2) {
      sides.push_back(key);
    }
  }

  for (size_t i = 0; i < sides.size(); ++i) {
    for (size_t j = i; j < sides.size(); ++j) {
      if (counts[sides[i]] >= 2 && counts[sides[j]] >= 2 && (i != j || counts[sides[i]] >= 4)) {
        long long area = sides[i] * sides[j];
        if (i == j && counts[sides[i]] < 4) continue;
        max_area = max(max_area, area);
      }
    }
  }

  cout << max_area << endl;
  return 0;
}
