n = int(input())
a = list(map(int, input().split()))
counts = {}
for x in a:
    counts[x] = counts.get(x, 0) + 1
pairs = []
for x in counts:
    if counts[x] >= 2:
        pairs.append(x)
pairs.sort(reverse=True)
max_area = 0
for i in range(len(pairs)):
    for j in range(i + 1, len(pairs)):
        area = pairs[i] * pairs[j]
        if counts[pairs[i]] >= 2 and counts[pairs[j]] >= 2:
            max_area = max(max_area, area)
        elif counts[pairs[i]] >= 2 and pairs[i] == pairs[j] and counts[pairs[i]] >=4:
            max_area = max(max_area, area)

print(max_area)

