#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    char s1[n + 1], s2[n + 1];
    scanf("%s %s", s1, s2);

    long long count = 0;
    long long *colors = (long long *)malloc((n + 1) * sizeof(long long));

    for (long long i = 0; i < 3; i++) {
        for (long long j = 0; j < 3; j++) {
            colors[0] = i;
            colors[1] = j;
            long long valid = 1;

            if (s1[0] == s2[0]) {
                if (i == j) valid = 0;
            }


            if (valid) {
                long long k = 2;
                while (k < n) {
                    long long cur = 0;
                    for (long long l = 0; l < 3; l++) {
                        if (l != colors[k-1] && (s1[k-1] == s1[k] ? l != colors[k-2] : 1 ) && (s2[k-1] == s2[k] ? l != colors[k-2] : 1)) {

                            cur++;
                        }
                    }

                    if (cur == 0) {
                        valid = 0;
                        break;
                    }
                    if (s1[k] == s1[k-1]) colors[k] = (colors[k-1] == 0 ? 1 : 0);
                    else if (s2[k] == s2[k-1]) colors[k] = (colors[k-1] == 0 ? 1 : 0);
                    else colors[k] = (colors[k-1] == 0 ? 1 : 0);
                    k++;


                }
                if (valid) count = (count + 1) % MOD;
            }
        }
    }

    printf("%lld\n", count);
    free(colors);
    return 0;
}
