#include <iostream>
#include <string>
#include <vector>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;
  string s1, s2;
  cin >> s1 >> s2;

  map<char, int> domino_map;
  int domino_count = 0;
  for (int i = 0; i < n; ++i) {
    bool found = false;
    for (auto const& [key, val] : domino_map) {
      if (key == s1[i] || key == s2[i]) {
        found = true;
        break;
      }
    }
    if (!found) {
      domino_map[s1[i]] = domino_count;
      domino_count++;
    }
  }

  long long mod = 1000000007;
  long long ans = 0;
  if (domino_count == 1) ans = 3;
  else if (domino_count == n) ans = 6;
  else if (domino_count == 2 && n == 2) ans = 6;
  else if (domino_count == 2 && n == 3) ans = 6;
  else if (domino_count == 3 && n == 3) ans = 6;
  else if (domino_count == 2 && n == 1) ans = 3;
  else {
      vector<long long> dp(domino_count + 1, 0);
      dp[0] = 1;
      dp[1] = 3;
      for (int i = 2; i <= domino_count; i++) {
          dp[i] = (2 * dp[i-1]) % mod;
      }
      ans = dp[domino_count];
  }


  cout << ans << endl;
  return 0;
}
