import java.util.*;
import java.lang.*;

class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s1 = sc.next();
        String s2 = sc.next();
        Map<Character, Integer> dominoMap = new HashMap<>();
        int dominoCount = 0;
        for (int i = 0; i < n; i++) {
            char c1 = s1.charAt(i);
            char c2 = s2.charAt(i);
            if (!dominoMap.containsKey(c1)) {
                dominoMap.put(c1, dominoCount++);
            }
            if (!dominoMap.containsKey(c2)) {
                dominoMap.put(c2, dominoCount++);
            }
        }

        long[][] dp = new long[dominoCount][3];
        Arrays.fill(dp[0], 1);

        for (int i = 1; i < dominoCount; i++) {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    if (k != j) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }
        long ans = 0;
        for (int i = 0; i < 3; i++) {
            ans = (ans + dp[dominoCount - 1][i]) % MOD;
        }
        System.out.println(ans);
    }
}
