            if c > 0:
                other_domino1 = s1[c-1] if r==0 else s2[c-1]
                if other_domino1 != domino:
                    adj[domino].append(other_domino1)
            if c < n -1:
                other_domino2 = s1[c+1] if r==0 else s2[c+1]
                if other_domino2 != domino:
                    adj[domino].append(other_domino2)
            if r == 0:
                other_domino3 = s2[c]
                if other_domino3 != domino:
                    adj[domino].append(other_domino3)



    ans = 1
    for domino in dominoes:
        ans = (ans * 3) % MOD


    print(ans)
