#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return *(long long *)a - *(long long *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    qsort(a, n, sizeof(long long), cmp);
    long long max_area = 0;
    for (int i = 0; i < n - 3; i++) {
        if (a[i] == a[i + 1] && a[i + 2] == a[i + 3]) {
            max_area = (a[i] * a[i + 2]);
            break;
        }
        if (a[i] == a[i+1] && i + 3 < n && a[i+2] == a[i+3]){
            max_area = (a[i] * a[i+2]);
            break;
        }
        if (a[i] == a[i+1] && i+3 < n){
            for (int j = i+2; j < n-1; j++){
                if (a[j] == a[j+1]){
                    max_area = (a[i]*a[j]);
                    break;
                }
            }

        }
    }
    printf("%lld\n", max_area);
    return 0;
}
