import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Map<Long, Integer> count = new HashMap<>();
        for (int i = 0; i < n; i++) {
            long a = sc.nextLong();
            count.put(a, count.getOrDefault(a, 0) + 1);
        }
        List<Long> lengths = new ArrayList<>(count.keySet());
        long maxArea = 0;
        for (int i = 0; i < lengths.size(); i++) {
            for (int j = i; j < lengths.size(); j++) {
                long len1 = lengths.get(i);
                long len2 = lengths.get(j);
                if (count.get(len1) >= 2 && count.get(len2) >= 2 && (i != j || count.get(len1) >= 4)) {
                    maxArea = Math.max(maxArea, len1 * len2);
                }
            }
        }
        System.out.println(maxArea);
    }
}
