#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    char s1[n + 1], s2[n + 1];
    scanf("%s %s", s1, s2);
    long long dp[n + 1][3][3];
    memset(dp, 0, sizeof(dp));
    dp[0][0][0] = 1;
    for (int i = 0; i < n; i++) {
        char c1 = s1[i], c2 = s2[i];
        if (c1 == c2) {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    if (j != k) {
                        for (int l = 0; l < 3; l++) {
                            if (l != j && l != k) {
                                dp[i + 1][k][l] = (dp[i + 1][k][l] + dp[i][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        } else {
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    if (j != k) {
                        for (int l = 0; l < 3; l++) {
                            if (l != j) {
                                dp[i + 1][k][l] = (dp[i + 1][k][l] + dp[i][j][k]) % MOD;
                            }
                        }
                        for (int l = 0; l < 3; l++) {
                            if (l != k) {
                                dp[i + 1][j][l] = (dp[i + 1][j][l] + dp[i][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        }
    }
    long long ans = 0;
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            ans = (ans + dp[n][i][j]) % MOD;
        }
    }
    printf("%lld\n", ans);
    return 0;
}
