MOD = 1000000007

def solve():
    n = int(input())
    s1 = input()
    s2 = input()

    adj = {}
    for i in range(n):
        if s1[i] not in adj:
            adj[s1[i]] = []
        if s2[i] not in adj:
            adj[s2[i]] = []
        if i > 0:
            if s1[i] != s1[i-1]:
                adj[s1[i]].append(s1[i-1])
                adj[s1[i-1]].append(s1[i])
            if s2[i] != s2[i-1]:
                adj[s2[i]].append(s2[i-1])
                adj[s2[i-1]].append(s2[i])
            if s1[i] != s2[i-1]:
                adj[s1[i]].append(s2[i-1])
                adj[s2[i-1]].append(s1[i])
            if s2[i] != s1[i-1]:
                adj[s2[i]].append(s1[i-1])
                adj[s1[i-1]].append(s2[i])


    dominoes = set(list(s1) + list(s2))

    color_count = {}
    for d in dominoes:
        color_count[d] = 3


    count = 1
    for d in dominoes:
        count = (count * color_count[d]) % MOD

    print(count)

