#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char S[H][W + 1];
    for (int i = 0; i < H; i++) {
        scanf("%s", S[i]);
    }
    int max_area = 0;
    for (int i = 0; i < (1 << H); i++) {
        for (int j = 0; j < (1 << W); j++) {
            char board[H][W];
            for (int k = 0; k < H; k++) {
                for (int l = 0; l < W; l++) {
                    board[k][l] = S[k][l];
                }
            }
            for (int k = 0; k < H; k++) {
                if ((i >> k) & 1) {
                    for (int l = 0; l < W; l++) {
                        board[k][l] = (board[k][l] == '#' ? '.' : '#');
                    }
                }
            }
            for (int k = 0; k < W; k++) {
                if ((j >> k) & 1) {
                    for (int l = 0; l < H; l++) {
                        board[l][k] = (board[l][k] == '#' ? '.' : '#');
                    }
                }
            }
            int cur_max_area = 0;
            for (int r1 = 0; r1 < H; r1++) {
                for (int c1 = 0; c1 < W; c1++) {
                    for (int r2 = r1; r2 < H; r2++) {
                        for (int c2 = c1; c2 < W; c2++) {
                            int all_black = 1;
                            for (int r = r1; r <= r2; r++) {
                                for (int c = c1; c <= c2; c++) {
                                    if (board[r][c] == '.') {
                                        all_black = 0;
                                        break;
                                    }
                                }
                                if (!all_black) break;
                            }
                            if (all_black) {
                                cur_max_area = (r2 - r1 + 1) * (c2 - c1 + 1);
                            }
                        }
                    }
                }
            }
            max_area = (max_area > cur_max_area) ? max_area : cur_max_area;
        }
    }
    printf("%d\n", max_area);
    return 0;
}
