#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<string> s(h);
  for (int i = 0; i < h; ++i) {
    cin >> s[i];
  }

  int max_area = 0;
  for (int i = 0; i < (1 << (h + w)); ++i) {
    vector<string> cur_s = s;
    for (int j = 0; j < h + w; ++j) {
      if ((i >> j) & 1) {
        if (j < h) {
          for (int k = 0; k < w; ++k) {
            cur_s[j][k] = (cur_s[j][k] == '#' ? '.' : '#');
          }
        } else {
          for (int k = 0; k < h; ++k) {
            cur_s[k][j - h] = (cur_s[k][j - h] == '#' ? '.' : '#');
          }
        }
      }
    }

    for (int r1 = 0; r1 < h; ++r1) {
      for (int c1 = 0; c1 < w; ++c1) {
        for (int r2 = r1; r2 < h; ++r2) {
          for (int c2 = c1; c2 < w; ++c2) {
            bool ok = true;
            for (int r = r1; r <= r2; ++r) {
              for (int c = c1; c <= c2; ++c) {
                if (cur_s[r][c] == '.') {
                  ok = false;
                  break;
                }
              }
              if (!ok) break;
            }
            if (ok) {
              max_area = max(max_area, (r2 - r1 + 1) * (c2 - c1 + 1));
            }
          }
        }
      }
    }
  }
  cout << max_area << endl;
  return 0;
}
