import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        char[][] grid = new char[H][W];
        for (int i = 0; i < H; i++) {
            String s = sc.next();
            for (int j = 0; j < W; j++) {
                grid[i][j] = s.charAt(j);
            }
        }
        int maxArea = 0;
        for (int i = 0; i < (1 << H); i++) {
            for (int j = 0; j < (1 << W); j++) {
                char[][] curGrid = new char[H][W];
                for (int k = 0; k < H; k++) {
                    System.arraycopy(grid[k], 0, curGrid[k], 0, W);
                }
                for (int k = 0; k < H; k++) {
                    if ((i >> k) % 2 == 1) {
                        for (int l = 0; l < W; l++) {
                            curGrid[k][l] = (curGrid[k][l] == '#' ? '.' : '#');
                        }
                    }
                }
                for (int k = 0; k < W; k++) {
                    if ((j >> k) % 2 == 1) {
                        for (int l = 0; l < H; l++) {
                            curGrid[l][k] = (curGrid[l][k] == '#' ? '.' : '#');
                        }
                    }
                }
                int curMaxArea = 0;
                for (int r1 = 0; r1 < H; r1++) {
                    for (int c1 = 0; c1 < W; c1++) {
                        for (int r2 = r1 + 1; r2 <= H; r2++) {
                            for (int c2 = c1 + 1; c2 <= W; c2++) {
                                boolean allBlack = true;
                                for (int r = r1; r < r2; r++) {
                                    for (int c = c1; c < c2; c++) {
                                        if (curGrid[r][c] == '.') {
                                            allBlack = false;
                                            break;
                                        }
                                    }
                                    if (!allBlack) break;
                                }
                                if (allBlack) {
                                    curMaxArea = Math.max(curMaxArea, (r2 - r1) * (c2 - c1));
                                }
                            }
                        }
                    }
                }
                maxArea = Math.max(maxArea, curMaxArea);
            }
        }
        System.out.println(maxArea);
    }
}
