#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int reversedN = 0;
    int temp = N;
    while (temp > 0) {
        reversedN = reversedN * 10 + temp % 10;
        temp /= 10;
    }
    if (N == reversedN) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
