#include <stdio.h>

int max(int a, int b) {
  return (a > b) ? a : b;
}

int min(int a, int b) {
  return (a < b) ? a : b;
}

int main() {
  int a, b, c, d;
  scanf("%d %d %d %d", &a, &b, &c, &d);
  int start = max(a, c);
  int end = min(b, d);
  int result = max(0, end - start);
  printf("%d\n", result);
  return 0;
}
