#include <stdio.h>

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

long long lcm(long long a, long long b) {
    return (a * b) / gcd(a, b);
}

int main() {
    int n;
    scanf("%d", &n);
    long long t[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &t[i]);
    }
    long long result = t[0];
    for (int i = 1; i < n; i++) {
        result = lcm(result, t[i]);
    }
    printf("%lld\n", result);
    return 0;
}
