#include <iostream>
#include <numeric>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

long long lcm(long long a, long long b) {
    return (a * b) / gcd(a, b);
}

int main() {
    int N;
    std::cin >> N;
    long long T[N];
    for (int i = 0; i < N; ++i)
        std::cin >> T[i];
    long long result = T[0];
    for (int i = 1; i < N; ++i)
        result = lcm(result, T[i]);
    std::cout << result << std::endl;
    return 0;
}
