import math

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return a * b // gcd(a, b)

N = int(input())
T = []
for _ in range(N):
    T.append(int(input()))

result = T[0]
for i in range(1, N):
    result = lcm(result, T[i])

print(result)
