#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAXN 100005
#define INF LLONG_MAX

typedef long long ll;

int main() {
    int n;
    scanf("%d", &n);

    ll edges[n - 1][3];
    for (int i = 0; i < n - 1; i++) {
        scanf("%lld %lld %lld", &edges[i][0], &edges[i][1], &edges[i][2]);
        edges[i][0]--; 
        edges[i][1]--; 
    }

    int q, k;
    scanf("%d %d", &q, &k);
    k--; 

    for (int i = 0; i < q; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        x--; 
        y--; 

        ll dist_x_k = INF;
        ll dist_y_k = INF;

        ll dist[n];
        for (int j = 0; j < n; j++) dist[j] = INF;
        dist[x] = 0;

        for (int j = 0; j < n -1; j++) {
            for (int l = 0; l < n - 1; l++) {
                if (dist[edges[l][0]] != INF && dist[edges[l][1]] > dist[edges[l][0]] + edges[l][2]) {
                    dist[edges[l][1]] = dist[edges[l][0]] + edges[l][2];
                }
                if (dist[edges[l][1]] != INF && dist[edges[l][0]] > dist[edges[l][1]] + edges[l][2]) {
                    dist[edges[l][0]] = dist[edges[l][1]] + edges[l][2];
                }
            }
        }
        dist_x_k = dist[k];

        for (int j = 0; j < n; j++) dist[j] = INF;
        dist[y] = 0;
        for (int j = 0; j < n -1; j++) {
            for (int l = 0; l < n - 1; l++) {
                if (dist[edges[l][0]] != INF && dist[edges[l][1]] > dist[edges[l][0]] + edges[l][2]) {
                    dist[edges[l][1]] = dist[edges[l][0]] + edges[l][2];
                }
                if (dist[edges[l][1]] != INF && dist[edges[l][0]] > dist[edges[l][1]] + edges[l][2]) {
                    dist[edges[l][0]] = dist[edges[l][1]] + edges[l][2];
                }
            }
        }
        dist_y_k = dist[k];

        if (dist_x_k == INF || dist_y_k == INF) {
            printf("-1\n");
        } else {
            printf("%lld\n", dist_x_k + dist_y_k);
        }
    }

    return 0;
}
