#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
  }

  vector<long long> c(n);
  for (int i = 0; i < n; ++i) {
    cin >> c[i];
  }

  long long count = 0;
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      for (int k = 0; k < n; ++k) {
        if (b[j] > a[i] && c[k] > b[j]) {
          count++;
        }
      }
    }
  }

  cout << count << endl;
  return 0;
}
