#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int count4 = 0, count2 = 0, count1 = 0;
    int *arr = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
        if (arr[i] % 4 == 0)
            count4++;
        else if (arr[i] % 2 == 0)
            count2++;
        else
            count1++;
    }
    if (count4 >= n - 1) {
        printf("Yes\n");
        return 0;
    }
    if (count4 + count2 >= n - 1 && count2 >= 1) {
        printf("Yes\n");
        return 0;
    }

    if (count4 + count2 >= n -1 && count2 >=2 && count1 >=1){
        printf("Yes\n");
        return 0;
    }

    if (count4 + count2 >= n -1 && count2 >=1 && count1>=0){
        printf("Yes\n");
        return 0;
    }
    printf("No\n");
    return 0;
}
