#include <stdio.h>

int main() {
    int H, W, N;
    scanf("%d %d", &H, &W);
    scanf("%d", &N);
    int a[N];
    int sum = 0;
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
        sum += a[i];
    }
    int grid[H][W];
    int count = 0;
    int color = 0;
    int r = 0, c = 0;
    int dr[] = {0, 1, 0, -1};
    int dc[] = {1, 0, -1, 0};
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            grid[i][j] = 0;
        }
    }
    for (int i = 0; i < N; i++) {
        int num = a[i];
        while (num > 0) {
            grid[r][c] = i + 1;
            num--;
            count++;
            int next_r = r + dr[0];
            int next_c = c + dc[0];
            if (next_r >= 0 && next_r < H && next_c >= 0 && next_c < W && grid[next_r][next_c] == 0) {
                r = next_r;
                c = next_c;
            } else {
                int dir = 0;
                while (dir < 4) {
                    next_r = r + dr[dir];
                    next_c = c + dc[dir];
                    if (next_r >= 0 && next_r < H && next_c >= 0 && next_c < W && grid[next_r][next_c] == 0) {
                        r = next_r;
                        c = next_c;
                        break;
                    }
                    dir++;
                }
            }
        }
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%d%c", grid[i][j], j == W - 1 ? '\n' : ' ');
        }
    }
    return 0;
}
