#include <iostream>
#include <vector>

using namespace std;

int main() {
  int h, w, n;
  cin >> h >> w >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<vector<int>> grid(h, vector<int>(w));
  long long count = 0;
  int color = 0;
  int r = 0, c = 0;

  while (count < h * w) {
    if (a[color] == 0) {
      color++;
      continue;
    }
    grid[r][c] = color + 1;
    a[color]--;
    count++;
    if (c + 1 < w && a[color] > 0) {
      c++;
    } else if (r + 1 < h && a[color] > 0) {
      r++;
      c = 0;
    } else {
      color++;
      r = 0;
      c = 0;
      for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
          if (grid[i][j] == 0) {
            r = i;
            c = j;
            break;
          }
        }
      }

    }
  }

  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      cout << grid[i][j] << (j == w - 1 ? "" : " ");
    }
    cout << endl;
  }

  return 0;
}
