H, W = map(int, input().split())
N = int(input())
a = list(map(int, input().split()))

grid = [[0 for _ in range(W)] for _ in range(H)]
count = 0
color_index = 0
r, c = 0, 0
dr = [0, 1, 0, -1]
dc = [1, 0, -1, 0]

for i in range(N):
    num_to_paint = a[i]
    stack = [(r, c)]
    while num_to_paint > 0:
        curr_r, curr_c = stack.pop()
        if 0 <= curr_r < H and 0 <= curr_c < W and grid[curr_r][curr_c] == 0:
            grid[curr_r][curr_c] = i + 1
            num_to_paint -= 1
            for j in range(4):
                next_r, next_c = curr_r + dr[j], curr_c + dc[j]
                stack.append((next_r, next_c))

        if num_to_paint == 0:
            break

        if stack == []:
            for nr in range(r,H):
                for nc in range(c, W):
                    if grid[nr][nc] == 0:
                        r, c = nr, nc
                        stack.append((r,c))
                        break
                if stack:
                    break

for row in grid:
    print(*row)

