#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<long long> a(n);
    int cnt4 = 0;
    int cnt2 = 0;
    int cnt1 = 0;
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        if (a[i] % 4 == 0) {
            cnt4++;
        } else if (a[i] % 2 == 0) {
            cnt2++;
        } else {
            cnt1++;
        }
    }

    if (cnt4 + cnt2 / 2 >= n -1) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
