#include <stdio.h>

int main() {
    int H, W, N;
    scanf("%d %d", &H, &W);
    scanf("%d", &N);
    int a[N];
    int sum = 0;
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
        sum += a[i];
    }
    int grid[H][W];
    int count = 0;
    int color = 0;
    int r = 0, c = 0;
    while (count < sum) {
        if (a[color] > 0) {
            grid[r][c] = color + 1;
            a[color]--;
            count++;
            if (c < W - 1) c++;
            else {
                r++;
                c = 0;
            }
        } else {
            color++;
        }
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%d ", grid[i][j]);
        }
        printf("\n");
    }
    return 0;
}
