#include <iostream>
#include <vector>

using namespace std;

int main() {
  int h, w, n;
  cin >> h >> w >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<vector<int>> grid(h, vector<int>(w));
  long long cur_count = 0;
  int cur_color = 0;
  int r = 0, c = 0;
  int dr[] = {0, 1, 0, -1};
  int dc[] = {1, 0, -1, 0};

  for (int i = 0; i < n; i++) {
    long long count = a[i];
    vector<pair<int, int>> q;
    q.push_back({r, c});
    grid[r][c] = i + 1;
    count--;
    int head = 0;

    while (count > 0) {
      pair<int, int> cur = q[head++];
      for (int j = 0; j < 4; j++) {
        int nr = cur.first + dr[j];
        int nc = cur.second + dc[j];
        if (nr >= 0 && nr < h && nc >= 0 && nc < w && grid[nr][nc] == 0) {
          grid[nr][nc] = i + 1;
          q.push_back({nr, nc});
          count--;
          if (count == 0) break;
        }
      }
      if (count == 0) break;
    }

    int next_r = r;
    int next_c = c;
    bool found = false;
    for(int nr = 0; nr < h; ++nr){
        for(int nc = 0; nc < w; ++nc){
            if(grid[nr][nc] == 0){
                next_r = nr;
                next_c = nc;
                found = true;
                break;
            }
        }
        if(found) break;
    }
    r = next_r;
    c = next_c;
  }


  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      cout << grid[i][j] << (j == w - 1 ? "" : " ");
    }
    cout << endl;
  }

  return 0;
}
