import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        int N = sc.nextInt();
        int[] a = new int[N];
        for (int i = 0; i < N; i++) {
            a[i] = sc.nextInt();
        }
        int[][] grid = new int[H][W];
        int count = 0;
        int color = 0;
        int r = 0, c = 0;
        for (int i = 0; i < N; i++) {
            int num = a[i];
            for (int j = 0; j < num; j++) {
                grid[r][c] = i + 1;
                count++;
                c++;
                if (c == W) {
                    c = 0;
                    r++;
                }
            }
        }
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                System.out.print(grid[i][j] + (j == W - 1 ? "" : " "));
            }
            System.out.println();
        }
    }
}
