#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    int *p = (int *)malloc(N * sizeof(int));
    for (int i = 0; i < N; i++) {
        scanf("%d", &p[i]);
    }
    int *q = (int *)malloc(N * sizeof(int));
    int q_index = 0;
    int p_index = 0;
    while (p_index < N) {
        int min_val = p[p_index];
        int min_index = p_index;
        for (int i = p_index + 1; i < N; i++) {
            if (p[i] < min_val) {
                min_val = p[i];
                min_index = i;
            }
        }
        if (min_index == p_index) {
            q[q_index++] = p[p_index++];
            q[q_index++] = p[p_index++];
        } else {
            int temp = p[p_index];
            for (int i = p_index; i < min_index; i++) {
                p[i] = p[i + 1];
            }
            p[min_index] = temp;
            q[q_index++] = p[p_index++];
            q[q_index++] = p[p_index++];
        }
    }
    for (int i = 0; i < N; i++) {
        printf("%d%c", q[i], (i == N - 1) ? '\n' : ' ');
    }
    free(p);
    free(q);
    return 0;
}
