#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> p(n);
    for (int i = 0; i < n; ++i) {
        cin >> p[i];
    }

    vector<int> best_q;
    vector<bool> used(n + 1, false);
    vector<int> q;
    vector<int> current_p = p;

    function<void(vector<int>)> solve = [&](vector<int> current_p) {
        if (current_p.empty()) {
            if (best_q.empty() || q < best_q) {
                best_q = q;
            }
            return;
        }

        for (int i = 0; i < current_p.size() - 1; ++i) {
            vector<int> next_p;
            for (int j = 0; j < current_p.size(); ++j) {
                if (j != i && j != i + 1) {
                    next_p.push_back(current_p[j]);
                }
            }
            vector<int> next_q = q;
            next_q.insert(next_q.begin(), current_p[i]);
            next_q.insert(next_q.begin() + 1, current_p[i + 1]);
            solve(next_p);

        }
    };

    solve(current_p);

    for (int i = 0; i < best_q.size(); ++i) {
        cout << best_q[i] << (i == best_q.size() - 1 ? "" : " ");
    }
    cout << endl;

    return 0;
}
