import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Integer> p = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            p.add(sc.nextInt());
        }
        List<Integer> q = new ArrayList<>();
        while (!p.isEmpty()) {
            int min = Integer.MAX_VALUE;
            int minIndex = -1;
            for (int i = 0; i < p.size() - 1; i++) {
                if (p.get(i) < min) {
                    min = p.get(i);
                    minIndex = i;
                }
            }
            q.add(p.get(minIndex));
            q.add(p.get(minIndex + 1));
            p.remove(minIndex + 1);
            p.remove(minIndex);
        }
        for (int i = 0; i < q.size(); i++) {
            System.out.print(q.get(i) + (i == q.size() - 1 ? "" : " "));
        }
        System.out.println();
    }
}
