#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int x[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &x[i]);
    }
    int count = 0;
    int arr[10000001] = {0};
    for (int i = 0; i < N; i++) {
        arr[x[i]] = 1;
    }
    for (int i = 1; i <= 10000000; i++) {
        if (arr[i] == 1) {
            int p = 3;
            while (1) {
                int flag = 0;
                if (i + p -1 <= 10000000) {
                    for (int j = i; j < i + p; j++) {
                        arr[j] = 1 - arr[j];
                    }
                    flag = 1;
                    count++;
                }

                if(flag == 0) break;
                int temp = 0;
                for (int k = i; k < i+p; k++){
                    if(arr[k] == 1) temp++;
                }
                if(temp == 0) break;
            }
        }
    }
    printf("%d\n", count);
    return 0;
}
