#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  vector<bool> cards(10000001, false);
  for (long long val : x) {
    cards[val] = true;
  }

  int count = 0;
  for (long long i = 1; i <= 10000000; ++i) {
    if (cards[i]) {
      bool found = false;
      for (long long p = 3; p <= 10000; ++p) {
        bool is_prime = true;
        for (long long j = 2; j * j <= p; ++j) {
          if (p % j == 0) {
            is_prime = false;
            break;
          }
        }
        if (is_prime && i + p -1 <= 10000000) {
          bool possible = true;
          for (long long j = i; j < i + p; ++j) {
            cards[j] = !cards[j];
          }
          count++;
          found = true;
          break;
        }
      }
    }
  }
  cout << count << endl;
  return 0;
}
