import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextInt();
        }
        Arrays.sort(x);
        int ans = 0;
        int cur = 0;
        for (int i = 0; i < n; i++) {
            if (x[i] > cur + 1) {
                ans += (x[i] - cur - 1) / 3 + 1;
                cur = x[i] - 1;
            } else if (x[i] == cur + 1){
                cur = x[i];
            }
        }
        if (cur < x[n - 1]){
            ans += (x[n-1] - cur) / 3 + 1;
        }

        System.out.println(ans);
    }
}
