def solve():
    n = int(input())
    x = list(map(int, input().split()))

    cards = [0] * (10**7 + 1)
    for val in x:
        cards[val] = 1

    ans = 0


    def check(arr):
        for val in arr:
            if val == 1:
                return False
        return True

    while not check(cards):
        ans += 1
        best_p = -1
        best_start = -1
        max_flipped = 0

        for p in range(3, 10001, 2):
          is_prime = True
          for i in range(3, int(p**0.5) + 1, 2):
            if p % i == 0:
              is_prime = False
              break
          if not is_prime:
            continue
          for start in range(1, len(cards) - p + 1):
            flipped_count = 0
            for i in range(start, start + p):
              if cards[i] == 1:
                flipped_count +=1

            if flipped_count > max_flipped:
              max_flipped = flipped_count
              best_p = p
              best_start = start

        for i in range(best_start, best_start + best_p):
            cards[i] = 1 - cards[i]

    print(ans)

solve()
