#include <stdio.h>
#include <limits.h>
#include <queue>

using namespace std;

int main() {
    int k;
    scanf("%d", &k);

    queue<pair<long long, int>> q;
    q.push({0, 0});

    long long min_sum = LLONG_MAX;
    int visited[100000] = {0};

    while (!q.empty()) {
        long long num = q.front().first;
        int sum = q.front().second;
        q.pop();

        if (num != 0 && num % k == 0) {
            min_sum = min(min_sum, sum);
            continue;
        }

        if (num > 1000000000) continue;

        for (int i = 0; i <= 9; ++i) {
            long long next_num = num * 10 + i;
            int next_sum = sum + i;


            q.push({next_num, next_sum});
        }
    }
    printf("%lld\n", min_sum);
    return 0;
}
