#include <iostream>

int countDivisibleByTwo(int n) {
    int count = 0;
    while (n % 2 == 0 && n > 0) {
        n /= 2;
        count++;
    }
    return count;
}

int main() {
    int n;
    std::cin >> n;
    int maxCount = -1;
    int result = 1;
    for (int i = 1; i <= n; ++i) {
        int count = countDivisibleByTwo(i);
        if (count > maxCount) {
            maxCount = count;
            result = i;
        }
    }
    std::cout << result << std::endl;
    return 0;
}
