import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int max_count = 0;
        int ans = 1;
        for (int i = 1; i <= n; i++) {
            int count = 0;
            int temp = i;
            while (temp % 2 == 0 && temp > 0) {
                count++;
                temp /= 2;
            }
            if (count > max_count) {
                max_count = count;
                ans = i;
            }
        }
        System.out.println(ans);
    }
}
