import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<Pair> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            edges.add(new Pair(u, v));
        }
        boolean possible = false;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < m; j++) {
                if (edges.get(i).first == 1 && edges.get(j).first == edges.get(i).second && edges.get(j).second == n) {
                    possible = true;
                    break;
                }
                if (edges.get(i).first == 1 && edges.get(j).second == edges.get(i).second && edges.get(j).first == n) {
                    possible = true;
                    break;
                }
                if (edges.get(i).second == 1 && edges.get(j).first == edges.get(i).first && edges.get(j).second == n) {
                    possible = true;
                    break;
                }
                if (edges.get(i).second == 1 && edges.get(j).second == edges.get(i).first && edges.get(j).first == n) {
                    possible = true;
                    break;
                }

            }
            if (possible) break;
        }
        if (possible) {
            System.out.println("POSSIBLE");
        } else {
            System.out.println("IMPOSSIBLE");
        }
    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }
    }
}
