#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int a[M], b[M];
    for (int i = 0; i < M; i++) {
        scanf("%d %d", &a[i], &b[i]);
    }
    int reachable[N + 1];
    for (int i = 1; i <= N; i++) {
        reachable[i] = 0;
    }
    reachable[1] = 1;
    for (int i = 0; i < M; i++) {
        if (reachable[a[i]] == 1) {
            reachable[b[i]] = 1;
        }
        if (reachable[b[i]] == 1) {
            reachable[a[i]] = 1;
        }
    }
    if (reachable[N] == 1) {
        printf("POSSIBLE\n");
    } else {
        printf("IMPOSSIBLE\n");
    }
    return 0;
}
