#include <iostream>
#include <vector>

int main() {
    long long k;
    std::cin >> k;
    long long n = 10;
    std::vector<long long> a(n);
    a[0] = k + n;
    for (int i = 1; i < n; ++i) {
        a[i] = 0;
    }
    std::cout << n << std::endl;
    for (int i = 0; i < n; ++i) {
        std::cout << a[i] << (i == n - 1 ? "" : " ");
    }
    std::cout << std::endl;
    return 0;
}
