#include <stdio.h>

int main() {
    long long n;
    scanf("%lld", &n);
    long long a[n];
    long long max_val = 0;
    long long sum = 0;
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
        sum += a[i];
        if (a[i] > max_val) {
            max_val = a[i];
        }
    }
    long long ans = 0;
    while (max_val >= n) {
        ans++;
        long long diff = max_val - (sum - max_val + n -1);
        if (diff <= 0) {
            break;
        }
        long long k = (max_val - (n-1)) / (n);
        if ((max_val - (n - 1)) % n != 0) {
            k++;
        }
        sum += (n-1) * k - max_val* k;
        max_val = (max_val - n *k) + (n-1) * k;

    }
    printf("%lld\n", ans);
    return 0;
}
