#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  while (true) {
    long long max_val = *max_element(a.begin(), a.end());
    if (max_val <= n - 1) {
      break;
    }

    long long max_index = -1;
    for (int i = 0; i < n; ++i) {
      if (a[i] == max_val) {
        max_index = i;
        break;
      }
    }

    a[max_index] -= n;
    for (int i = 0; i < n; ++i) {
      if (i != max_index) {
        a[i]++;
      }
    }
    count++;
  }
  cout << count << endl;
  return 0;
}
