import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long[] a = new long[(int) n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long ans = 0;
        while (true) {
            long max = -1;
            int maxIndex = -1;
            for (int i = 0; i < n; i++) {
                if (a[i] > max) {
                    max = a[i];
                    maxIndex = i;
                }
            }
            if (max <= n - 1) {
                break;
            }
            a[maxIndex] -= n;
            for (int i = 0; i < n; i++) {
                if (i != maxIndex) {
                    a[i]++;
                }
            }
            ans++;
        }
        System.out.println(ans);
    }
}
