import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt() - 1;
        }
        boolean possible = false;
        for (int i = 0; i < (1 << n); i++) {
            int[] a = new int[n];
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    a[j] = 1;
                } else {
                    a[j] = 0;
                }
            }
            boolean ok = true;
            for (int j = 0; j < n; j++) {
                if (a[j] == a[p[j]]) {
                    ok = false;
                    break;
                }
                if (a[j] > 0) {
                    boolean found = false;
                    for (int k = 0; k < n; k++) {
                        if (p[k] == j && a[k] == a[j] - 1) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        ok = false;
                        break;
                    }
                }
            }
            if (ok) {
                possible = true;
                break;
            }
        }
        if (possible) {
            System.out.println("POSSIBLE");
        } else {
            System.out.println("IMPOSSIBLE");
        }
    }
}
