n = int(input())
p = list(map(int, input().split()))
p = [x - 1 for x in p]
graph = [[] for _ in range(n)]
for i in range(n):
    graph[p[i]].append(i)

def solve():
    for i in range(1 << n):
        a = []
        for j in range(n):
            if (i >> j) & 1:
                a.append(1)
            else:
                a.append(0)

        valid = True
        for j in range(n):
            if a[p[j]] == a[j]:
                valid = False
                break

            counts = {}
            for k in graph[j]:
                counts[a[k]] = counts.get(a[k], 0) + 1

            if a[j] > 0:
                for k in range(a[j]):
                    if k not in counts:
                        valid = False
                        break
            if not valid:
                break

        if valid:
            return "POSSIBLE"
    return "IMPOSSIBLE"

print(solve())
