#include <stdio.h>
#include <stdlib.h>

int gcd(int a, int b) {
    if (b == 0) {
        return a;
    }
    return gcd(b, a % b);
}

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int *A = (int *)malloc(N * sizeof(int));
    int g = 0;
    for (int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
        g = gcd(g, A[i]);
    }
    free(A);
    if (K % g == 0) {
        printf("POSSIBLE\n");
    } else {
        printf("IMPOSSIBLE\n");
    }
    return 0;
}
