#include <iostream>
#include <numeric>
#include <vector>

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int n, k;
    std::cin >> n >> k;
    std::vector<int> a(n);
    for (int i = 0; i < n; ++i)
        std::cin >> a[i];
    int g = 0;
    for (int i = 0; i < n; ++i)
        g = gcd(g, a[i]);
    if (k % g == 0)
        std::cout << "POSSIBLE" << std::endl;
    else
        std::cout << "IMPOSSIBLE" << std::endl;
    return 0;
}

