import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long k = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long gcd = 0;
        for (long x : a) {
            gcd = gcd(gcd, x);
        }
        boolean possible = false;
        for (long x : a) {
            if ((x - k) % gcd == 0 && x >= k) {
                possible = true;
                break;
            }
        }
        if (possible) {
            System.out.println("POSSIBLE");
        } else {
            System.out.println("IMPOSSIBLE");
        }
    }
    public static long gcd(long a, long b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }
}
